const isVowel = (c) => "aeiou".includes(c)

function maxVowels(s: string, k: number): number {
  let i = 0
  let max = 0
  let count = 0

  for (; i < k; i++) {
    let cur = s[i]

    if (isVowel(cur)) {
      count++
    }
  }

  max = Math.max(count, max)

  for(; i < s.length; i++) {
    let cur = s[i]
    let last = s[i - k]

    if (isVowel(cur)) {
      count++
    }

    if (isVowel(last)) {
      count--
    }

    max = Math.max(count, max)
  }

 return max
};

