function longestOnes(nums: number[], k: number): number {
  let res = 0
  let right = 0
  let left = 0
  let zeros = 0

  while(right < nums.length) {
    if (nums[right] === 0) {
      zeros++
    }

    while(zeros > k) {
      if (nums[left++] === 0) {
        zeros--
      }
    }

    res = Math.max(res, right - left + 1)
    right++
  }

  return res
};

