function longestSubarray(nums: number[]): number {
  let res = 0
  let left = 0
  let right = 0

  for (; right < nums.length; right++) {
    res += nums[right]

    if (res < right - left) {
      res -= nums[left]
      left++
    }
  }

  return right - left - 1
