const getJson = () => 
new Promise(async (resolve) => {
  const total = 147 // 可以换成你们的仓库总数哦
  const perPage = 100 // 一次请求条数，固定的
  const size = Math.ceil(total / perPage) 
  let res = []
  for (let i = 1; i <= size; i++) {
    const url = `http://gitlab.xxx.com/groups/xxx/-/children.json?page=${i}&per_page=${perPage}`
    const response = await fetch(url, {
      method: 'get',
      credentials: 'include' // 不写这个属性，请求的时候不会携带cookie哦！
    })
    const data = await response.json()
    res.push(...data)
    if(i >= size){
      resolve(res)
    }
  }
})


const getRepos = async ()=>{
  const json = await getJson()
  console.log('仓库总数',json.length);
  const res = json.reduce((prev, curr) => {
    // 这个正则是匹配仓库名的，front是我们公司前端组的组名，你们换成你们自己公司的哈，接口上有的噢
    const name = /\/front\/([a-zA-Z-_0-9]*)/.exec(curr.relative_path)[1]
    prev += `"${name}"\n`
    return prev
  }, '')
  return res
}


console.log(await getRepos())

#! /bin/bash

read -p "是否在当前文件夹下克隆代码(yes/no):" flag

if [ "$flag" != "yes" ];then
echo "请输入yes" exit
fi

echo "正在当前文件夹下克隆前端所有代码.....请稍后"

# 定义一个包含多个仓库URL的列表
# (把刚刚复制的数据粘贴将来即可)
repos=(
  "repo-name1"
  "repo-name2"
  ...
 )

# 遍历数组中的每个URL，并执行 git clone
for repo in "${repos[@]}"
do
  # 如果仓库已拉取过
  if [ -d "$repo" ] && [ -d "$repo/.git" ]; then
    # 进入该目录
    cd "$repo"
    # 切换到master
    git checkout master
    # 拉取最新代码
    git pull
    # 切回到原始目录，以便处理下一个仓库
    cd ..
  else 
  # 仓库未拉取过，clone下来
   git clone "http://gitlab.xxx.com/xxx/$repo.git"
  fi
done
