const ffmpeg = require("fluent-ffmpeg");

function convertMp4ToMp3(inputPath, outputPath) {
  return new Promise((resolve, reject) => {
    ffmpeg(inputPath)
      .output(outputPath)
      .audioCodec("libmp3lame") // ä½¿ç”¨ MP3 ç¼–è§£ç å™¨
      .on("end", () => {
        console.log("è½¬æ¢å®Œæˆ");
        resolve();
      })
      .on("error", (err) => {
        console.error("è½¬æ¢é”™è¯¯:", err);
        reject(err);
      })
      .run();
  });
}

